import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import fetchAPI from '../services/index';
import heartWhite from '../images/whiteHeartIcon.svg';
// import heartBlack from '../images/blackHeartIcon.svg';
// import share from '../images/shareIcon.svg';
import Share from '../components/Recipe/Share';
import { createIngredientsList } from '../utils';
import '../css/ComidasEmProcessoCSS.css';

function BebidasEmProcesso() {
  const { id } = useParams();
  console.log(id);
  const history = useHistory();

  const [drink, setDrink] = useState({});
  const [loading, setLoading] = useState(false);
  const [finished, setFinished] = useState(true);
  const [listIngredients, setListIngredients] = useState([]);

  const storage = JSON.parse(localStorage.getItem('inProgressRecipes'));
  const [indexChecked, setIndexChecked] = useState(storage.meals[id] || []);

  useEffect(() => {
    setLoading(true);
    const fetchDrink = async () => {
      const data = await fetchAPI(`www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`);
      console.log(data);
      setDrink(data);

      const ingredientes = createIngredientsList(data);
      const filter = ingredientes.filter((item) => item[0] !== undefined);
      setListIngredients(filter);

      setLoading(false);
    };

    fetchDrink();
  }, []);

  useEffect(() => {
    const previousIngredientes = JSON.parse(localStorage.getItem('inProgressRecipes'));

    const newIngredientes = {
      ...previousIngredientes,
      cocktails: {
        ...previousIngredientes.cocktails,
        [id]: indexChecked,
      },
    };

    localStorage.setItem('inProgressRecipes', JSON.stringify(newIngredientes));

    setFinished(listIngredients.length !== indexChecked.length);
  }, [indexChecked, listIngredients]);

  function checkedStyle({ target: { checked } }, ingredient) {
    if (checked) {
      setIndexChecked((previous) => ([...previous, ingredient]));
    } else if (!checked) {
      const filterChecked = indexChecked.filter((item) => item !== ingredient);
      setIndexChecked(filterChecked);
    }
  }

  function renderList() {
    console.log(listIngredients);
    return listIngredients.map((item, index) => (
      <label
        htmlFor="checks"
        key={ index }
        className={ indexChecked.includes(item[0]) ? 'text-decoration' : 'no-decoration' }
        data-testid={ `${index}-ingredient-step` }
      >
        <input
          type="checkbox"
          id="checks"
          onClick={ (ev) => checkedStyle(ev, item[0]) }
          checked={ indexChecked.includes(item[0]) }
        />
        { `${item[0]} - ${item[1]}` }
      </label>
    ));
  }

  if (loading) return <p>carregando</p>;

  return (
    <section>
      <img
        src={ drink.strDrinkThum }
        data-testid="recipe-photo"
        alt="imagem da refeição"
      />
      <h3 data-testid="recipe-title">{ drink.strDrink }</h3>
      <p data-testid="recipe-category">{ drink.strCategory }</p>

      {/* <button type="button" data-testid="share-btn">
        <img src={ share } alt="icone compartilhar" />
      </button> */}
      <Share />
      <button type="button" data-testid="favorite-btn">
        <img src={ heartWhite } alt="icone coração" />
      </button>

      { renderList() }

      <p data-testid="recipe-category">{ drink.strInstructions }</p>

      <button
        type="button"
        data-testid="finish-recipe-btn"
        disabled={ finished }
        onClick={ () => history.push('/receitas-feitas') }
      >
        finalizar
      </button>
    </section>
  );
}

export default BebidasEmProcesso;
