import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import fetchAPI from '../services/index';

function ComidasEmProcesso() {
  const { id } = useParams();
  console.log(id);
  const [meal, setMeal] = useState({});

  useEffect(() => {
    const data = fetchAPI(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`);
    setMeal(data);
    console.log(data);
  }, [id]);

  console.log(meal);
  return (
    <section>
      <img src={ meal.strMealThumb } data-testid="recipe-photo" alt="imagem da refeição" />
      <h3 data-testid="recipe-title">{ meal.strMeal }</h3>
      <p data-testid="recipe-category">{ meal.strMeal }</p>
      <button type="button" data-testid="share-btn">compartilhar</button>
      <button type="button" data-testid="favorite-btn">favoritar</button>

      <p data-testid="recipe-category">{ meal.strInstructions }</p>
      <button type="button" data-testid="finish-recipe-btn">finalizar</button>
    </section>
  );
}

export default ComidasEmProcesso;
